var sortedListToBST = function(head) {
    if (head == null) {
        return null;
    }

    let pre = null;
    let slow = fast = head;

    while (fast != null && fast.next != null) {
        pre = slow;
        slow = slow.next;
        fast = fast.next.next;
    }

    if (pre != null) {
        pre.next = null;
    } else {
        head = null;
    }
    const next = slow.next;
    slow.next = null;
    const root = new TreeNode(slow.val);
    root.left = sortedListToBST(head);
    root.right = sortedListToBST(next);
    return root;
};
