### 代码
```js
class MinHeap{
  constructor() {
    this.data = [];
  }

  peek() {
    return this.data[0];
  }

  poll() {
    //   const first = this.data.shift();
    //   this.bubbleDown(0);
    //   return first;
    const first = this.data[0];
    const last =  this.data.pop();
    this.data[0] = last;
    this.bubbleDown(0);

    return first;
  }

  offer(val) {
    this.data.push(val);
    this.bubbleUp(this.data.length - 1);
  }

  bubbleUp(i) {
    while (i > 0) {
      const parent = Math.floor((i - 1) / 2);
      if (this.data[parent][1] > this.data[i][1]) {
        this.swap(parent, i);
        i = parent;
      } else {
          break;
      }
    }
  }

  bubbleDown(i) {
    while(true) {
      let left = i * 2 + 1;
      let right = i * 2 + 2;
      let min = i;
      if (left < this.size() && this.data[min][1] > this.data[left][1]) {
        min = left;
      }

      if (right < this.size() && this.data[min][1] > this.data[right][1]) {
        min = right;
      }

      if (min !== i) {
        this.swap(i, min);
        i = min;
      } else {
        break;
      }
    }
  }

  size() {
    return this.data.length;
  }

  swap(i, j) {
    [this.data[i], this.data[j]] = [this.data[j], this.data[i]];
  }
}
