var solution = function(nums, k) {
    const floorMod = (a, b) => {
        return (a % b + b) % b;
    }
  const map = new Map();
  map.set(0, -1);
  const n = nums.length;
  let pre = 0;
  let ans = n;
  let target = 0;
  for (let i = 0; i < n; i++) {
      target += nums[i];
  }
  target %= k;
  for (let i = 0; i < n; i++) {
    pre += nums[i];
    const remainder = pre % k;
    map.set(remainder, i);
    const prevSum = floorMod(pre - target, k)
    if (map.has(prevSum)) {
      ans = Math.min(ans, i - map.get(prevSum))
    } 
  }
  return ans == n ? -1 : ans;
}
