var possibleBipartition = function(n, dislikes) {
    const graph = new Array(n).fill(0).map(() => new Array(n).fill(0));
    const colors = new Array(n).fill(0);
    const dfs = (i, color, n) => {
        colors[i] = color;
        for (let j = 0; j < n; j++) {
            if (graph[i][j] == 1) {
                if (colors[j] == color) {
                    return false;
                }
                if (colors[j] == 0 && !dfs(j, -1 * color, n)) {
                    return false;
                }
            }
        }
        return true;
    }
    for (let [a, b] of dislikes) {
        graph[a - 1][b - 1] = 1;
        graph[b - 1][a - 1] = 1;
    }
    for (let i = 0; i < n; i++) {
        if (colors[i] == 0 && !dfs(i, 1, n)) {
            return false;
        }
    }
    return true;
};

