    }

    offer(val) {
        this.data.push(val);
        this.bubbleUp(this.size() - 1);
    }

    poll() {
        const result = this.data[0];
        const last = this.data.pop();
        if (this.size() !== 0) {
            this.data[0] = last;
            this.bubbleDown(0);
        }
        return result;
    }

    bubbleUp(i) {
        while (i > 0) {
            const parent = Math.floor((i - 1) / 2);
            if (this.compare(parent, i)) {
                this.swap(parent, i);
                i = parent;
            } else {
                break;
            }
        }
    }

    bubbleDown(i) {
        while (true) {
            const left = 2 * i + 1;
            const right = 2 * i + 2;
            let min = i;
            if (i < this.size() && this.data[left] && this.compare(min, left)) {
                min = left;
            }

            if (i < this.size() && this.data[right] && this.compare(min, right)) {
                min = right;
            }

            if (min !== i) {
                this.swap(i, min);
                i = min;
            } else {
                break;
            }
        }
    }

    size() {
        return this.data.length;
    }

    swap(i, j) {
        const temp = this.data[i];
        this.data[i] = this.data[j];
        this.data[j] = temp;
    }
}

var getOrder = function(tasks) {
    const n = tasks.length;
    const indices = tasks.map((task, index) => ({
        index,
        start: task[0]
    })).sort((a, b) => a.start - b.start).map((val) => val.index);

    const heap = new minHeap();
    let timestamp = 0;
    let ptr = 0;
    let ans = [];
    for (let i = 0; i < n; i++) {
        if (heap.data.length == 0) {
            timestamp = Math.max(timestamp, tasks[indices[ptr]][0]);
        }

        while(ptr < n && tasks[indices[ptr]][0] <= timestamp) {
            heap.offer([tasks[indices[ptr]][1], indices[ptr]]);
            ptr++;
        }

        const [process, index] = heap.poll();
        timestamp += process;
        ans.push(index);
    }
    return ans;
};
```

### 复杂度分析
- 时间复杂度： O(nlogn)
- 空间复杂度：O(n)

