class Solution:
    def lengthOfLongestSubstring(self, s: str) -> int:
        if s == "": return 0
        left = right = 0
        window = {}
        res = 0
        while right < len(s):
            c = s[right]
            if c not in window:
                window[c] = 1
            else:
                window[c] += 1
            while window[c] > 1:
