/**
 * @param {number[]} arr
 * @return {number}
 */
var maxChunksToSorted = function(arr) {
    //定义最大数据栈
    let res=[]
    for(let i = 0; i < arr.length; i++) {
        if(res.length==0){
            res.push(arr[i])
        }else{
            if(arr[i]>=res[res.length-1]){
                res.push(arr[i])
            }else{
                let max=res[res.length-1]
                while(arr[i]<res[res.length-1]){
                    res.pop()
