var lengthOfLongestSubstring = function(s) {
    const hashMap = new Set()
    let longestSubstring = 0;
    let slow = 0;
    for(let fast = 0; fast < s.length; fast++){
        let char = s[fast]  
        while(hashMap.has(char) && slow < s.length){
            hashMap.delete(s[slow])
            slow++
        }
        longestSubstring = Math.max(longestSubstring, fast - slow + 1)
        hashMap.add(char)
    }
    return longestSubstring
};
