var removeDuplicates = function(nums) {
    // 定义读、写指针，开始时均指向数组起点
    let writePoint = 0;
    let readPoint = 0;
    // 数组未被遍历至末尾
    while(readPoint < nums.length) {
        // 当读写指针内容不相等时
        if(nums[writePoint] !== nums[readPoint]) {
            // 移动写指针
            writePoint ++;
            // 改写数组相应的值
            nums[writePoint] = nums[readPoint]
        }
        readPoint ++;
    }
    return writePoint + 1
};
