class Solution {
  public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> out;
        int sum = 0;
        for(int i = num.size()-1; i >= 0 || k > 0; i--) {
            if(i >= 0){
                sum = k + num[i];
                out.push_back(sum % 10);
                k = sum /10;
            }else{
                out.push_back(k % 10);
                k = k /10;
