class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        vector<int> idx;
        vector<int> dst;
        int position=0;
        while((position=s.find(c,position))!=string::npos)
        {
            idx.push_back(position);
            position++;
        }
        int current;
        int next;
        if(!idx.empty()){
            current = idx[0];
            if(idx.size()>1){
               next = idx[1];
            }else{
              next = current;  
            }
        }
        int num_idx = 2;
        int min_dst = 0;
        for(int i = 0;i< s.size();i++){
            if(i>current){
                min_dst = min(abs(i-current),abs(i-next));
                dst.push_back(min_dst);
                if(abs(i-current)>=abs(i-next)){
                  current = next;
                  if(num_idx < idx.size()){
                    next = idx[num_idx]; 
                    num_idx++;                   
                  }else{
                      next =current;
                  }
                }   
            }else if(i==current){
                 dst.push_back(0);
            }else{
                dst.push_back(abs(i-current));
            }
        }
        return dst;
    }
};
```
