var maxChunksToSorted = function(arr) {
    let sum1 = 0;
    let sum2 = 0;
    let res = 0;
    let arr2 = [...arr].sort((a, b) => a - b);
    for (let i in arr) {
        sum1 = sum1 + arr[i];
        sum2 = sum2 + arr2[i];
        if (sum1 == sum2) {
            res += 1;
            sum1 = 0;
            sum2 = 0;
        }
    }
    return res;
};
