/**
 * @param {number[][]} points
 * @return {number}
 */
var numberOfBoomerangs = function(points) {
    let ans = 0;
    for (let p of points) {
        const map = new Map();
        for (let q of points) {
            const dis = Math.pow((p[0] - q[0]), 2) + Math.pow((p[1] - q[1]), 2); 
            map.set(dis, (map.get(dis) || 0) + 1);
        }
        for (let [_, value] of map.entries()) {
            ans += (value - 1) * value;
        }
    }
    return ans;
};

