/**
 * @param {number} x
 * @return {number}
 */
var mySqrt = function(x) {
  let left = 0;
  let right = x;
  while (left <= right) {
    let mid = Math.floor((left + right) / 2);
    if (mid > Math.floor(x / mid)) {
      right = mid - 1;
    } else {
      left = mid + 1;
    }
  }
  return right; 
};

