class Solution {
    public List<Integer> addToArrayForm(int[] res, int K) {
        List<Integer> Arr = new ArrayList<>();
        int n = res.length;
        int i = n-1;
        int carry = 0;
        int sum = 0;
        while(i >= 0 || K != 0){
            int x = i >= 0 ? res[i] : 0;
            int y = K >= 0 ? K%10 : 0;
            sum = x + y +carry;
            carry = sum / 10;
            K = K / 10;
