class Solution {
    public String decodeString(String s) {
        char[] str = s.toCharArray();
        Queue<Character> q = new LinkedList<>();
        for (char c : str) {
            q.add(c);
        }
        return process(q);
    }
    
    public String process(Queue<Character> q) {
        int num = 0;
        StringBuilder sb = new StringBuilder();
        while (!q.isEmpty()) {
            char c = q.poll();
            if (Character.isDigit(c)) {
                num *= 10;
                num += c - '0';
            }
            else if (c == '[') {
                String s = process(q);
                for (int i = 0; i < num; i++) {
                    sb.append(s);
                }
                num = 0;
            }
            else if (c == ']') {
                break;
            }
            else {
                sb.append(c);
            }
        }
        return sb.toString();
    }
}
