public class Solution {
    public ListNode detectCycle(ListNode head) {
        if (head == null) return null;
        ListNode fast = head, slow = head;
        while (true) {
            // if any pointer reaches null, there is no cycle
            slow = slow.next;
            if (slow == null) return null;
            fast = fast.next;
            if (fast == null) return null;
            fast = fast.next;
            if (fast == null) return null;
            
            // if two pointers meet, there is a cycle
            if (slow == fast) {
                break;
            }
        }
        // slow pointer restarts at head and fast remains its own position, find the cycle
        slow = head;
        while (slow != fast) {
            slow = slow.next;
            fast = fast.next;
        }
        return slow;
    }
}
