class LRUCache {
    public Map<Integer, Node> cache;
    public int capacity;
    public Node head, tail;
    
    class Node {
        public int key;
        public int val;
        public Node prev;
        public Node next;
        
        public Node(){};
        public Node(int k, int v, Node p, Node n) {
            this.key = k;
            this.val = v;
            this.prev = p;
            this.next = n;
        }
    }
        
    public LRUCache(int capacity) {
        this.cache = new HashMap<>();
        this.capacity = capacity;
        this.head = new Node();
        this.tail = new Node();
        head.prev = null;
        head.next = tail;
        tail.prev = head;
        tail.next = null;
    }
    
    public int get(int key) {
        if (cache.containsKey(key)) {
            Node node = cache.get(key); 
            moveToTail(node);
            return node.val;
        }
        else {
            return -1;
        }
    }
    
    public void put(int key, int value) {
        if (cache.containsKey(key)) {
            Node node = cache.get(key);
            node.val = value;
            moveToTail(node);
            return;
        }
        Node node = new Node();
        node.key = key;
        node.val = value;
        cache.put(key, node);
        addNode(node);
        int size = cache.size();
        
        if (size > capacity) {
            Node toRemove = head.next;
            removeNode(toRemove);
            cache.remove(toRemove.key, toRemove);
        }
    }

    private void addNode(Node node) {
        Node tmp = tail.prev;
        tmp.next = node;
        node.prev = tmp;
        node.next = tail;
        tail.prev = node;
    }

    private void removeNode(Node node) {
        Node p = node.prev;
        Node n = node.next;
        p.next = n;
        n.prev = p;
    }

    public void moveToTail(Node node) {
        removeNode(node);
        addNode(node);
    }
}
```
