class Solution {
    public int numberOfRounds(String loginTime, String logoutTime) {
        int s_h = Integer.parseInt(loginTime.substring(0, 2));
        int e_h = Integer.parseInt(logoutTime.substring(0, 2));
        int s_m = Integer.parseInt(loginTime.substring(3));
        int e_m = Integer.parseInt(logoutTime.substring(3));
        
        int start = s_h * 60 + s_m;
        int end = e_h * 60 + e_m;
        
        if (start > end) {
            end += 24 * 60;
        }
        
        if (start % 15 != 0) {
            start += 15 - (start % 15);
        }
        
        if (end % 15 != 0) {
            end -= end % 15;
        }
        
        int res = (end - start) / 15;
        return res >= 0 ? res : 0;
    }
}
