class Solution {
    public int reversePairs(int[] nums) {
        int n = nums.length, ans = 0;
        List<Long> list = new ArrayList<>();
        for (int i=0; i<n; i++) {
            int hi = binarySearch(list, 2L*nums[i]+1);
            ans += (i-hi);
            int idx = Collections.binarySearch(list, (long) nums[i]);
            if (idx<0) list.add(-idx-1, (long) nums[i]);
            else list.add(idx, (long) nums[i]);
        }
        return ans;
    }
    public int binarySearch(List<Long> list, long v) {
        int lo = 0, hi = list.size()-1, ans = list.size();
        while (lo<=hi) {
            int mid = (lo+hi)/2;
            if (list.get(mid)>=v) {
                ans = mid;
                hi = mid-1;
            }
            else lo = mid+1;
        }
        return ans;
    }
}
