class CustomStack {
public:
    vector<int> stk;
    int top;

    CustomStack(int maxSize) {
        stk.resize(maxSize);
        top = -1;
    }

    void push(int x) {
        if (top != stk.size() - 1) {
            ++top;
            stk[top] = x;
        }
    }

    int pop() {
        if (top == -1) {
            return -1;
        }
        --top;
        return stk[top + 1];
    }

    void increment(int k, int val) {
        int lim = min(k, top + 1);
        for (int i = 0; i < lim; ++i) {
            stk[i] += val;
        }
    }
};

## 题目
字符串解码
https://leetcode-cn.com/problems/decode-string/
## 思路
     可以结合分配律，先将括号里的字母结合起来

## 代码
``` 
class Solution {
public:
    string decodeString(string s) {
         int len = s.size();
        int num = 0;
        stack<int> numstack;
        stack<string> strstack;
        string cur = "";
        string result = "";
        for(int i=0; i<len; ++i)
        {
            if(s[i]>='0' && s[i]<='9')
            {
                num = 10*num + s[i] - '0';
            }
            else if(s[i] == '[')
            {
                numstack.push(num);
                strstack.push(cur);
                num = 0;
                cur.clear();
            }
            else if((s[i]>='a' && s[i]<='z') || (s[i]>='A' && s[i]<='Z'))
                cur += s[i];
            else if(s[i] == ']')
            {
                int k = numstack.top();
                numstack.pop();
                for(int j=0; j<k; ++j)
                    strstack.top() += cur;
                cur = strstack.top();
                strstack.pop();
            }
        }
        result = result + cur;
        return result;     
    }
};
