class Solution {
public:
    int maxChunksToSorted(vector<int>& arr) {
        int n = arr.size();
        vector<int> premax(n), sumin(n);
        // 前缀最大
        for (int i = 0; i < n; i++) {
            if (i == 0) premax[i] = arr[i];
            else premax[i] = max(premax[i - 1], arr[i]);
        }
        // 后缀最小
        for (int i = n - 1; i >= 0; i--) {
            if (i == n - 1) sumin[i] = arr[i];
            else sumin[i] = min(sumin[i + 1], arr[i]);
        }
        int ans = 1;
        // 如果当前位置的前缀最大值小于等于下一个位置的后缀最小，说明当前位置小于等于后面所有位置，可分一块，ans++
        for (int i = 0; i < n - 1; i++) {
            if (premax[i] <= sumin[i + 1]) ans++;
        }
        return ans;
    }
};
