 代码
struct DoubleLinkList {
	DoubleLinkList* pre;
	DoubleLinkList* next;
	int key;
	int value;
	DoubleLinkList(int key, int value) : key(key), value(value), pre(NULL), next(NULL) {};
};

class LRUCache {
public:
	// 表头
	DoubleLinkList* head;
	// 表尾
	DoubleLinkList* tail;
	// 缓存，记录key所对应的链表节点
	map<int, DoubleLinkList*> memory;
	// 容量
	int cacheCapacity = 0;
	LRUCache(int capacity) {
		// 初始化
		cacheCapacity = capacity;
		head = new DoubleLinkList(-1, -1);
		tail = new DoubleLinkList(-1, -1);
		head->next = tail;
		tail->pre = head;
	}

	int get(int key) {
		// 如果缓存中查找到该key, 则需更新该key为最近使用（即放到表尾）
		if (memory.find(key) != memory.end()) {
			DoubleLinkList* node = memory[key];
			// 从链表中移除该节点
			removeNode(node);
			// 将该节点放至表尾
			pushNode(node);
			return node->value;
		}
		return -1;
	}

	void put(int key, int value) {
		// 如果缓存中有当前key
		if (memory.find(key) != memory.end()) {
			DoubleLinkList* node = memory[key];
			// 从链表中移除该节点
			removeNode(node);
			// 将该节点放至表尾
			pushNode(node);
			// 修改节点对应的值
			node->value = value;
			return;
		}
		// 如果内存已经放满
		if (memory.size() == cacheCapacity) {
			// 获取链表头结点的key
			int topKey = head->next->key;
			// 移除链表中的首个节点，并从memory中移除该key
			removeNode(head->next);
			memory.erase(topKey);
		}
		// 表尾放置新key
		DoubleLinkList* node = new DoubleLinkList(key, value);
		pushNode(node);
		// 存入缓存
		memory[key] = node;
	}
	// 从链表中移除节点
	void removeNode(DoubleLinkList* node) {
		// 将该节点从链表中移除
		node->pre->next = node->next;
		node->next->pre = node->pre;
	}
	// 表尾插入结点
	void pushNode(DoubleLinkList* node) {
		node->pre = tail->pre;
		tail->pre->next = node;
		node->next = tail;
		tail->pre = node;
	}
};

