class Solution {
    public String decodeString(String s) {
        if (s.length() == 1) return s;

        StringBuilder decoded = new StringBuilder();
        Stack<Integer> numStack = new Stack<>();

        Stack<StringBuilder> stack = new Stack<>();
        int index = 0;
        int curNum = 0;
        StringBuilder curS = new StringBuilder();

        while (index < s.length()) {
            if (Character.isDigit(s.charAt(index))) {             
                curNum = curNum * 10 + s.charAt(index) - '0';
                index++;
            }
            else if(s.charAt(index) == '[') {
                numStack.push(curNum);
                curNum = 0;

                stack.push(curS);
                curS = new StringBuilder();

                index++;
            }
            else if (Character.isLetter(s.charAt(index))) {
                curS.append(s.charAt(index));
                index++;
            }
            else if (s.charAt(index) == ']') {

                StringBuilder temp = curS;
                int freq = numStack.pop();
                curS = stack.pop();

                for (int i = 0; i < freq; i++) {
                    curS.append(temp);
                }
                index++;
            }

        }
        return curS.toString();
    }
}

