class Solution {
    public int maxChunksToSorted(int[] arr) {
        Stack<Integer> monoStack = new Stack<>();
        for (int num : arr) {
            if (monoStack.isEmpty() || monoStack.peek() <= num) {
                monoStack.push(num);
            }
            else {
                int curMax = monoStack.peek();
                while (!monoStack.isEmpty() && monoStack.peek() > num) {
                    monoStack.pop();
                }
                monoStack.push(curMax);
            }
        }
        return monoStack.size();
    }
