class LRUCache {

    class Node {
        int key;
        int val;
        Node pre;
        Node next;

        Node(int key, int val, Node pre, Node next) {
            this.key = key;
            this.val = val;
            this.pre = pre;
            this.next = next;
        }
    }

    int capacity;
    Map<Integer, Node> keyNodeMap;
    Node head;
    Node tail;

    public LRUCache(int capacity) {
        this.capacity = capacity;
        keyNodeMap = new HashMap<>();
        head = new Node(-1, -1, null, null);
        tail = new Node(-1, -1, null, null);
        connect(head, tail);
    }

    private void connect(Node n1, Node n2) {
        n1.next = n2;
        n2.pre = n1;
    }

    public int get(int key) {
        if (keyNodeMap.containsKey(key)) {
            Node target = keyNodeMap.get(key);
            Node preTarget = target.pre;
            Node nextTarget = target.next;
            connect(preTarget, nextTarget);

            Node headNext = head.next;
            connect(head, target);
            connect(target, headNext);
            return target.val;
        }
        return -1;
    }

    public void put(int key, int value) {
        if (keyNodeMap.containsKey(key)) {
            Node target = keyNodeMap.get(key);
            target.val = value;

            Node preTarget = target.pre;
            Node nextTarget = target.next;
            connect(preTarget, nextTarget);

            Node headNext = head.next;
            connect(head, target);
            connect(target, headNext);
            return;
        }
        if (capacity == 0) { // remove 1 from tail
            Node toRm = tail.pre;
            Node newTailPre = toRm.pre;
            connect(newTailPre, tail);
            keyNodeMap.remove(toRm.key);
            capacity++;
        }
        // add new node to head and map
        Node newNode = new Node(key, value, null, null);
        Node headNext = head.next;
        connect(head, newNode);
        connect(newNode, headNext);
        keyNodeMap.put(key, newNode);
        capacity--;
    }
}

