class Solution {
    public int maxDepth(TreeNode root) {
        return postOrder(root);
    }

    private int postOrder(TreeNode root) {
        if (root == null) return 0;
        int leftMaxD = postOrder(root.left);
        int rightMaxD = postOrder(root.right);

        return 1 + Math.max(leftMaxD, rightMaxD);
    }
}
