class Solution {
    public int sumNumbers(TreeNode root) {
        if (root == null) return 0;
        int[] total = new int[1];
        preorder(root, 0, total);
        return total[0];
    }

    private void preorder(TreeNode curNode, int curSum, int[] total) {
        if (curNode == null) return;

        curSum = curSum * 10 + curNode.val;
        if (curNode.left == null && curNode.right == null) {
            total[0] += curSum;
            return;  
        }

        preorder(curNode.left, curSum, total);
        preorder(curNode.right, curSum, total);    
    }
}
