public class Codec {

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        if (root == null) return "null";
        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);
        StringBuilder serialized = new StringBuilder();
        while (!queue.isEmpty()) {
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                TreeNode cur = queue.poll();
                if (cur == null) {
                    serialized.append("#null");
                } else {
                    serialized.append("#" + cur.val);
                    queue.offer(cur.left);
                    queue.offer(cur.right);
                }
            }
        }
        //System.out.println(serialized.toString()); // #1#2#3#null#null#4#5#null#null#null#null. root = [1,2,3,null,null,4,5]
        return serialized.toString();
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        if (data.equals("null")) return null;

        String[] tokens = data.split("#");// tokens[1]=1, tokens[0] = ""

        TreeNode root = new TreeNode(Integer.parseInt(tokens[1]));
        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);
        int index = 2;

        while (!queue.isEmpty()) {
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                TreeNode cur = queue.poll();

                if (tokens[index].equals("null")) {
                    cur.left = null;
                } else {
                    cur.left = new TreeNode(Integer.parseInt(tokens[index]));
                    queue.offer(cur.left);
                }

                index++;

                if (tokens[index].equals("null")) {
                    cur.right = null;
                } else {
                    cur.right = new TreeNode(Integer.parseInt(tokens[index]));
                    queue.offer(cur.right);
                }

                index++; // do not forget this line
            }
        }
        return root;
    }
}

