class Solution {
    public int[] topKFrequent(int[] nums, int k) {
        Map<Integer, Integer> valFreqMap = new HashMap<>();
        // O(nums.length) time
        for (int num : nums) {
            valFreqMap.put(num, valFreqMap.getOrDefault(num, 0) + 1);
        }
        PriorityQueue<Map.Entry<Integer, Integer>> minHeap = new PriorityQueue<>((a, b) -> (a.getValue() - b.getValue()));
        // O(number of unique num) * O(logk)
        for (Map.Entry<Integer, Integer> entry : valFreqMap.entrySet()) {
            minHeap.offer(entry);
            if (minHeap.size() > k) {
                minHeap.poll();
            }
        }

        // O(k)
        int[] res = new int[k];
        for (int i = 0; i < k; i++) {
            res[i] = minHeap.poll().getKey();
        }
        return res;
    }
}
