/*
Plan: get all pair-wise distances?  --> no idea later

map: distance - count
[i, j], [i, k]

for the current i, same distance, count - x, -> i can form x pairs of this distance with the remaining points. we can draw j and k from this set.

Time: O(n^2), n = points.length
Space: O(number of unique distances)
*/

class Solution {
    public int numberOfBoomerangs(int[][] points) {
        if (points.length <= 2) return 0;

        int count = 0, dist = 0;
        // for each possible i
        for (int i = 0; i < points.length; i++) {
            Map<Integer, Integer> distFreqMap = new HashMap<>();
            for (int j = 0; j < points.length; j++) {
                // same point
                if (i == j) {
                    continue;
                }
                dist = getDis(points[i], points[j]);
                distFreqMap.put(dist, distFreqMap.getOrDefault(dist, 0) + 1);
            }
            for (int value : distFreqMap.values()) {
                count += value * (value - 1);
            }

        }

        return count;    
    }

    private int getDis(int[] point1, int[] point2) {
        return (point1[0] - point2[0]) * (point1[0] - point2[0]) + (point1[1] - point2[1]) * (point1[1] - point2[1]);
    }
}
