class Solution {
    public int lengthOfLongestSubstring1(String s) {
        int l = 0, r = 0, maxLen = 0;
        Map<Character, Integer> charFreqMap = new HashMap<>();
        while (r < s.length()) {
            char rightChar = s.charAt(r);
            charFreqMap.put(rightChar, charFreqMap.getOrDefault(rightChar, 0) + 1);
            r++;
            while (charFreqMap.get(rightChar) >= 2) {
                char leftChar = s.charAt(l);
                charFreqMap.put(leftChar, charFreqMap.get(leftChar) - 1);

                if (charFreqMap.get(leftChar) == 0) {
                    charFreqMap.remove(leftChar);
                }
                l++;
            }
            maxLen = Math.max(maxLen, r - l);
        }
        return maxLen;
    }

    // optimized using a set
    public int lengthOfLongestSubstring(String s) {
        int l = 0, r = 0;
        int maxLen = 0;
        Set<Character> visited = new HashSet<>();

        while (r < s.length()) {
            char rightChar = s.charAt(r);
            while (visited.contains(rightChar)) {
                visited.remove(s.charAt(l));
                l++;
            }

            visited.add(rightChar);
            maxLen = Math.max(maxLen, visited.size());

            r++;
        }
        return maxLen;
    }
}
