class Solution {
    public int[] maxSlidingWindow(int[] nums, int k) {
        if (k == 1) return nums;
        int[] maxes = new int[nums.length - k + 1];
        int windowIndex = 0;
        ArrayDeque<Integer> indicesQueue = new ArrayDeque<>();
        for (int i = 0; i < nums.length; i++) {
            // window size within
            while (!indicesQueue.isEmpty() && i - indicesQueue.peekFirst() + 1 > k) {
                indicesQueue.pollFirst();
            }
            // monotonically decreasing property check-up
            while (!indicesQueue.isEmpty() && nums[indicesQueue.peekLast()] < nums[i]) {
                indicesQueue.pollLast();
            }

            // after the previous while, guarantee to have if not empty, nums[indexStack.peekLast()] >= nums[i]
            indicesQueue.addLast(i);
            // add to windows
            if (i >= k - 1) {
                maxes[windowIndex++] = nums[indicesQueue.peekFirst()]; // use val here!
            }
        }
        return maxes;

    }
}


