class Solution {
    public int mySqrt(int x) {
        if (x <= 1) return x; // edge case
        int l = 1, r = x;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (mid <= x / mid) { // one candidate, look right to check more
                l = mid + 1;
            }
            else { 
                r = mid - 1;
            }
        }
        return r;
    }
}
