[2,2].  [0,7].      [1,2].  [1111].   -> 


[2][2] [0][7].  


[2,2].  [0,7]. 
   i.    j


temp:0,2,2,7
*/
    private void merge(int[] nums, int left, int mid, int right) {
        int[] temp = new int[right - left + 1];
        int i = left, j = mid + 1; // [left, mid], [mid + 1, right]
        int curIndex = 0;
        while (i <= mid && j <= right) {
            if (nums[i] <= nums[j]) {
                temp[curIndex++] = nums[i];
                i++;
            } else {
                temp[curIndex++] = nums[j];
                j++;
            }
