public int[] stack;
    public int size = 0;
    public int maxSize;

    public CustomStack(int maxSize) {
        this.maxSize = maxSize;
        stack = new int[maxSize];
    }

    public void push(int x) {
        if(size == maxSize)
            return;
        stack[size++] = x;
    }

    public int pop() {
        if (size == 0)
            return -1;
        return stack[size--];
    }

    public void increment(int k, int val) {
        int i = 0;
        while(i < size && i < k){
            stack[i] = stack[i] + val;
            i += 1;
        }
    }
