class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> result;
        int len = num.size();
        for (int i = len - 1; i >= 0; i--) {
            int temp = num[i] + k % 10;
            k = k / 10;
            if (temp >= 10) {
                k += 1;
                temp = temp % 10;
            }
            result.push_back(temp);
        }
        while (k != 0) {
            result.push_back(k % 10);
            k = k / 10;
        }
        reverse(result.begin(), result.end());
        return result;
    }
};
```
