class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        vector<int> ans(s.size(), 0);
        int left = INT_MAX;
        int right = INT_MAX;
        for(int i = 0; i < s.size(); i ++) {
            if (s[i] == c) {
                left = i;
            }
            ans[i] = i - left;
        }
        for(int i = s.size() - 1; i >= 0; i --) {
            if (s[i] == c) {
                right = i;
            }
            ans[i] = min(abs(ans[i]), right - i);
        }
        return ans;
    }
};
