func shortestDistanceToACharacter(s string, c byte) []int {
	res := make([]int, len(s))

	// prevent overflow
	left, right := math.MinInt32/2, math.MaxInt32/2

	for i := 0; i < len(s); i++ {
		if s[i] == c {
			left = i
		}
		res[i] = i - left
	}

	for i := len(s) - 1; i >= 0; i-- {
		if s[i] == c {
			right = i
		}
		res[i] = min(res[i], right-i)
	}

