func detectCycle(head *ListNode) *ListNode {
    // 创建头结点和尾结点
    slow, fast := head, head

    // 快慢指针
    for fast != nil {
        slow = slow.Next
        if fast.Next == nil {
            return nil
        }
        fast = fast.Next.Next
        //
        if fast == slow {
            p := head
            for p != slow {
                p = p.Next
                slow = slow.Next
            }
            return p
        }
    }
    return nil
}
