    def pop(self) -> int:
        if not self.empty():
            if self.outStack:
                return self.outStack.pop()
            else:
                while self.inStack:
                    self.outStack.append(self.inStack.pop())
                return self.outStack.pop()
        else:
            return 0

    def peek(self) -> int:
        if self.empty():
            return 0
        if not self.outStack:            
            while self.inStack:
                self.outStack.append(self.inStack.pop())
        val = self.pop()
        self.outStack.append(val)
        return val



    def empty(self) -> bool:
        if not (self.inStack or self.outStack):
            return True
        return False

