class LRUCache:

    def __init__(self, capacity: int):
        self.dict = {}
        self.ord = []
        self.cap = capacity

    def get(self, key: int) -> int:
        if key in self.dict:           
            self.ord.remove(key)
            self.ord.append(key)
            return self.dict[key]
        else:
            return -1

    def put(self, key: int, value: int) -> None:
        self.dict[key] = value
        if key in self.ord:
            self.ord.remove(key)
            self.ord.append(key)
        else:
            if len(self.ord)==self.cap:
                k = self.ord.pop(0)
                del self.dict[k]
                self.ord.append(key)
            else:
                self.ord.append(key)




