class Solution:
    def getOrder(self, tasks: List[List[int]]) -> List[int]:
        t = copy.deepcopy(tasks)
        stack = []
        ans = []
        time = 0
        doing = 0
        expectTime = 0
        def trans(L:list):
            index = t.index(L)
            return (L[1],index)
        while tasks or stack:
