class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if(head == null){
            return head;
        }
        if(k == 0){
            return head;
        }
        ListNode dummy = head;
        int len = 0;
        while(head != null){
            head = head.next;
            len++;
        }
        int index = k%len;

        ListNode slow = dummy;
        ListNode fast = dummy;
        for(int i=0;i<index;i++){
            fast = fast.next;
        }
        ListNode newHead = fast;

        while(fast.next != null){
            slow = slow.next;
            fast = fast.next;
        }

        fast.next = dummy;
        dummy = slow.next;
        slow.next = null;
        return dummy;

    }
}
