class MyQueue:

    def __init__(self):
        self.pushstack = []
        self.popstack = []

    def push(self, x: int) -> None:
        self.pushstack.append(x)

    def pop(self) -> int:
        if self.popstack:
            ans = self.popstack.pop()
            return ans
        while self.pushstack:
            temp = self.pushstack.pop()
            self.popstack.append(temp)

        return self.popstack.pop()

    def peek(self) -> int:
        if self.popstack:
            ans = self.popstack[-1]
            return ans
        while self.pushstack:
            temp = self.pushstack.pop()
            self.popstack.append(temp)

        return self.popstack[-1]

    def empty(self) -> bool:
        return self.pushstack==[] and self.popstack==[]
