function fastSlowPointer(head, k) {
  /*
    1、判断链表的长度和 k 值是否合法
  */
  if (!head || !head.next || k === 0) {
    return head;
  }

  // 获取链表的长度
  let linkedListLength = 0;
  let now = head;
  while (now) {
    now = now.next;
    linkedListLength ++;
  }

  // 当前的 k 值获取的是链表的第几位
  k = k % linkedListLength;

  // 制造快慢指针
  let slow = (fast = head);
  while(fast.next) {
    if (k-- <= 0) {
      // 慢指针
      slow = slow.next;
    }
    // 快指针
    fast = fast.next;
  }

  // 开始查数
  fast.next = head;
  let res = slow.next;
  slow.next = null;

  return res;
}

