function swapPairs(head) {
  // 当链表为空或者链表只有一个元素的时候，直接返回该链表
  if (!head || !head.next) {
    return head;
  }

  // 准备循环的素材
  let res = head.next;
  let now = head;
  let preNode = new ListNode();
  preNode.next = head;

  while(now && now.next) {
    // 创建第二个节点和尾节点
    let secondNode = now.next;
    let tailNode = secondNode.next;

    // 两个节点两两交换
    now.next = tailNode;
    secondNode.next = now;
    preNode.next = secondNode;

    // 创建下一次循环的条件 ～ 前置节点
    preNode = now;
    now = tailNode;
  }

  return res;
}

