// 题目地址：https://leetcode-cn.com/problems/serialize-and-deserialize-binary-tree/

// 序列化和反序列化
function serialize(root) {
  const queue = [root];
  let res = [];

  while (queue.length) {
    // 删除数组的第一项并返回删除的内容
    const root = queue.shift();

    if (root) {
      res.push(root.val);

      queue.push(root.left);
      queue.push(root.right);
    } else {
      res.push('#');
    }
  }

  return res.join(',');
}

function deserialize(data) {
  if (data === '#') {
    return null;
  }

  const res = data.split(',');
  const root = new TreeNode(res[0]);
  const queue = [root];
  let cursor = 1;

  // 当光标的值不小于序列化数组的程度时，说明完成遍历
  while (cursor < res.length) {
    const node = queue.shift();
    const leftValue = res[cursor];
    const rightValue = res[cursor + 1];

    if (leftValue !== '#') {
      const leftNode = new TreeNode(leftValue);
      node.left = leftNode;
      queue.push(leftNode);
    }

    if (rightValue !== '#') {
      const rightNode = new TreeNode(rightValue);
      node.right = rightNode;
      queue.push(rightNode);
    }

    cursor += 2;
  }

  return root;
}
