class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> res = new LinkedList<>();
        int len = num.length;
        for (int i = len - 1; i >= 0; i--) {
            res.add(0, (num[i] + k) % 10);
            k = (num[i] + k) / 10;
        }
        while (k != 0) {
            res.add(0, k % 10);
            k /= 10;
        }
        return res;
    }
}
