class Solution {
    public int numberOfRounds(String loginTime, String logoutTime) {
        int res = 0;
        int h = Integer.parseInt(loginTime.substring(0, 2));
        int m = Integer.parseInt(loginTime.substring(3, 5));
        int hh = Integer.parseInt(logoutTime.substring(0, 2));
        int mm = Integer.parseInt(logoutTime.substring(3, 5));
        if (h > hh || (h == hh && m > mm)) {
            res += (23 - h + hh) * 4;
        } else {
            res += (hh - h - 1) * 4;
        }
        res += (60 - m) / 15;
        res += mm / 15;
        return res >= 0 ? res : 0;
    }
}
